mapper.select:
SELECT id, email, password, nickname, created_at
FROM member
WHERE email = #{email}

SELECT id, email, password, nickname, created_at
FROM member
WHERE id = #{id}

mapper.update:
UPDATE member
SET email = #{email},
    password = #{password},
    nickname = #{nickname}
WHERE id = #{id}

mapper.select:
SELECT COUNT(*)
FROM member
WHERE email = #{email}

SELECT COUNT(*)
FROM member
WHERE email = #{email} AND id != #{id}
